        }
        this.widgetStatus.emit({
            type: UserIdentitySecurityCenterActionType.PasswordChangeFailed,
        });
    }
    showNotification(template, type) {
        this.notificationService.showNotification({
            message: template,
            modifier: type,
            ttl: this.notificationDelay,
        });
    }
