        const control = this.getControl(fieldName);
        return control && (control.valid || control.untouched);
    }
    hasError(field, type) {
        const fieldControl = this.getControl(field);
        if (!fieldControl || !fieldControl.errors) {
            return;
        }
        return fieldControl.errors[type];
    }
}
