import { PaginationType } from './pagination-type.model';
import { SortDirection } from './sort-direction.model';
/**
 * Common configuration model for all journeys
 *
 * @internal
 */
export interface JourneyConfiguration {
    /** Absolute filter datepicker limit (yyyy-mm-dd) */
    absoluteDateLimit: string;
    /** Notification dismiss time in seconds (defaults to 5) */
    notificationDismissTime: number;
    /** PaginationType will decide whether the user will see load more or page number style pagination */
    paginationType: PaginationType;
    /** Number of statements per page */
    pageSize: number;
    /** Relative filter datepicker limit (months) */
    relativeDateLimit: number;
    /** Sorting direction (ASC, DESC) */
    sortingDirection: SortDirection;
    /** Sorting order (date, description, category) */
    sortingOrder: string;
    /** Show/Hide the category column */
    showCategory: boolean;
}
