export interface ChallengeSmsOtpData {
    /**
     * Data relating to SMS OTP challenge
     */
    data: any;
    /**
     * The masked phone number the request was sent to
     */
    phoneNumber: string;
    /**
     * Time until next resend request available
     */
    nextOtpTime: number;
    /**
     * The expected length of the OTP provided in the SMS
     */
    expectedOtpLength: number;
    /**
     * Number of remaining attempts to enter correct OTP
     */
    remainingAuthenticationAttempts?: number;
    /**
     * Number of resend requests left
     */
    remainingOtpRequests?: number;
}
export interface SmsOtpResponse {
    /**
     * The OTP response to the SMS OTP challenge
     */
    otp: string;
}
