import { ColorHelper, ScaleType } from '@swimlane/ngx-charts';
export interface ChartDataItem {
    name: string | Date;
    value: number;
}
export interface ChartDataItemExtended extends ChartDataItem {
    [key: string]: any;
}
export interface ChartColorItem {
    name: string;
    value: string;
    icon: string;
}
export interface LegendOptions {
    scaleType: ScaleType;
    domain: string[] | undefined;
    colors: ColorHelper | undefined;
    title: string;
}
export interface Arc {
    data: ChartDataItemExtended;
    endAngle: number;
    index: number;
    padAngle: number;
    pos: [number, number];
    startAngle: number;
    value: number;
    edge: [number, number];
}
