import { ProductSummaryItem } from '@backbase/data-ang/arrangements';
import { ContactsGetResponseBodyItem } from '@backbase/data-ang/contact-manager';
import { BaseObject } from './config.model';
/**
 * United type of ExternalAccount, Contact, Arrangement
 */
export interface AccountItem extends BaseObject<any> {
    uuid: string;
    name: string;
    currency: string;
    number: string;
    balance: number;
    kind: string;
    externalTransferAllowed: boolean;
    kindDisplay?: string;
    debitAccount: boolean;
    creditAccount: boolean;
    productKindName?: string;
    defaultRecord: ProductSummaryItem | ContactsGetResponseBodyItem;
}
export interface ExternalAccount {
    uuid: string;
    name: string;
    number: string;
    kind: 'externalAccount';
    productKindName: string;
}
export interface Contact {
    uuid: string;
    name: string;
    number: string;
    defaultRecord: ContactsGetResponseBodyItem;
}
export interface TransferAmount {
    currency: string;
    amount: string;
    type?: string;
}
export interface Arrangement {
    uuid: string;
    name: string;
    number: string;
    currency?: string;
    balance?: number;
    kind?: string;
    externalTransferAllowed?: boolean;
    kindDisplay?: string;
    productKindName?: string;
    debitAccount?: boolean;
    creditAccount?: boolean;
    defaultRecord?: ProductSummaryItem;
}
