export interface ChallengeOobDeviceData {
    /**
     * Data relating to oob device challenge
     */
    data: OobDeviceData;
}
export interface OobDeviceData {
    /**
     * Data sent from capability via the Identity provider
     */
    txnData?: object;
    /**
     * The information of the oob device
     */
    deviceInformation: OobDeviceInfo;
    /**
     * The information for the notification
     */
    pushNotificationInformation: OobNotificationInfo;
}
export interface OobDeviceInfo {
    /**
     * The vendor of the oob deivce
     */
    vendor: string;
    /**
     * The model of the oob deivce
     */
    model: string;
    /**
     * The friendlyName of the oob deivce
     */
    friendlyName: string;
    [key: string]: any;
}
export interface OobNotificationInfo {
    /**
     * The remaining time until the user can request a new notification
     */
    nextResendSeconds: number;
    /**
     * The remaining resend attempts the user has
     */
    resendAttemptsLeft: number;
    /**
     * The status of the last resend attempt
     */
    sendSuccessful?: boolean;
}
