export interface ChallengeHardTokenData {
    /**
     * Data relating to the hard token challenge
     */
    data: HardTokenData;
    /**
     * Number of remaining attempts to enter correct OTP
     */
    remainingAuthenticationAttempts?: number;
}
export interface HardTokenResponse {
    /**
     * The OTP response to the hard token challenge
     */
    otp: string;
    /**
     * The code entered into the hard token
     */
    code?: string;
}
export interface HardTokenData {
    /**
     * Data sent from capability via the Identity provider
     */
    txnData: HardTokenTransactionData;
    /**
     * Details of the hard token vendor
     */
    vendorDetails: HardTokenVendorDetails;
    /**
     * The code to enter into the hard token
     */
    code?: string;
}
export interface HardTokenTransactionData {
    /**
     * The transaction payment amount required for entry into hard token
     */
    paymentAmount?: string | number;
    [key: string]: any;
}
export interface HardTokenVendorDetails {
    /**
     * The model of the hard token vendor
     */
    model: string;
    [key: string]: any;
}
