import { LoadMoreMethod } from '@backbase/payment-orders-ang';
import { SchemeName } from '@backbase/data-ang/stop-checks';
export interface StopChecksJourneyConfiguration {
    /**
     * Comma separated list of available currencies
     */
    currencies: string[];
    /**
     * Number of payments per page
     */
    pageSize: number;
    /**
     * Max. number of navigation pages
     */
    maxNavPages: number;
    /**
     * Number of seconds to dismiss the notification
     */
    notificationDismissTime: number;
    /**
     * Select pagination type
     */
    paginationType: LoadMoreMethod;
    /**
     * Checks range number
     */
    checksRangeNumber: number;
    /**
     * Flag to render header part that includes title and button
     */
    shouldDisplayHeading: boolean;
    /**
     * Scheme Name
     */
    schemeName: SchemeName;
}
